
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n1 = 89;
		int n2 = 60;
		//operadores bitwise realizam testes bit a bit igual tabela verdade 0 falso e 1 verdadeiro
		//& operador "e"
		System.out.println(n1 & n2);
		//operador | "ou" 
		System.out.println(n1 | n2);
		//operador ^ "ou exclusivo" só será vedadeiro quando somente um for verdadeiro  
		System.out.println(n1 ^ n2);
		
		//neste caso este aplicativo mostra o resultado dos testes de verdadeiro e falso com os numeros 
		//89 e 60, que são transformados em binario pelo computador e depois escritos novamente em decimal
	}

}
